mapper.select:
		select `r_Id`,`r_Student_No`,`r_Stu_Id`,`r_Exam_Date`,`p_Course`,`r_Classs`,`r_Score`,`r_Pass`,`r_Note` from s_student_results

		  		   and r_Id = #{rId} 
		  		   and r_Student_No = #{rStudentNo} 
		  		   and r_Stu_Id = #{rStuId} 
		  		   and r_Exam_Date = #{rExamDate} 
		  		   and p_Course = #{pCourse} 
		  		   and r_Classs = #{rClasss} 
		  		   and r_Score = #{rScore} 
		  		   and r_Pass = #{rPass} 
		  		   and r_Note = #{rNote} 



                order by ${sort} ${order}


                order by r_Id desc



			limit #{offset}, #{limit}

select count(*) from s_student_results

  		   and r_Id = #{rId} 
  		   and r_Student_No = #{rStudentNo} 
  		   and r_Stu_Id = #{rStuId} 
  		   and r_Exam_Date = #{rExamDate} 
  		   and p_Course = #{pCourse} 
  		   and r_Classs = #{rClasss} 
  		   and r_Score = #{rScore} 
  		   and r_Pass = #{rPass} 
  		   and r_Note = #{rNote}
