mapper.select:
		select `a_Id`,`a_Student_No`,`a_Tea_Id`,`a_Start_Date`,`a_End_Date`,`a_Attendance_Type`,`a_Note` from s_tea_attendance

		  		   and a_Id = #{aId} 
		  		   and a_Student_No = #{aStudentNo} 
		  		   and a_Tea_Id = #{aTeaId} 
		  		   and a_Start_Date = #{aStartDate} 
		  		   and a_End_Date = #{aEndDate} 
		  		   and a_Attendance_Type = #{aAttendanceType} 
		  		   and a_Note = #{aNote} 



                order by ${sort} ${order}


                order by a_Id desc



			limit #{offset}, #{limit}

select count(*) from s_tea_attendance

  		   and a_Id = #{aId} 
  		   and a_Student_No = #{aStudentNo} 
  		   and a_Tea_Id = #{aTeaId} 
  		   and a_Start_Date = #{aStartDate} 
